plugins {
  id("com.gradle.enterprise").version("3.17.5")
}

rootProject.name = "worblehat"

include(":worblehat-domain")
include(":worblehat-web")

gradleEnterprise {
    buildScan {
        termsOfServiceUrl = "https://gradle.com/terms-of-service"
        termsOfServiceAgree = "yes"

        publishAlways()
    }
}

